require 'mini_magick'\n\n   def pdf_to_images(pdf_path, output_dir)\n     MiniMagick::Tool::Convert.new do |convert|\n       convert.density(300) # Set the resolution\n       convert.quality(100) # Set the quality\n       convert << pdf_path\n       convert << "#{output_dir}/output.png"\n     end\n\n     images = Dir.glob("#{output_dir}/output-*.png")\n     images.each_with_index do |image_path, index|\n       puts "Generated Image: #{image_path}"\n     end\n   end\n\n   pdf_path = 'path/to/your/document.pdf'\n   output_dir = 'path/to/output/directory'\n   pdf_to_images(pdf_path, output_dir)