class PdfController < ApplicationController\n  def generate_pdf\n    user_id = current_user.id\n    pdf_template_path = Rails.root.join('path', 'to', 'template.pdf')\n    output_path = Rails.root.join('path', 'to', 'output.pdf')\n    \n    # Use a library like Prawn or CombinePDF to manipulate the PDF\n    # Example using CombinePDF\n    pdf = CombinePDF.load(pdf_template_path)\n    pdf.pages.each do |page|\n      # Add the user_id to the field (this is just a placeholder, actual implementation depends on the library you use)\n      page.textbox(user_id.to_s, at: [100, 100], height: 10, width: 100) \n    end\n    pdf.save(output_path)\n    \n    send_file output_path, filename: 'user_file.pdf', type: 'application/pdf'\n  end\nend