class NotesController < ApplicationController\n  def index\n    @notes = Note.all\n  end\n\n  def show\n    @note = Note.find(params[:id])\n  end\n\n  def new\n    @note = Note.new\n  end\n\n  def create\n    @note = Note.new(note_params)\n    if @note.save\n      redirect_to @note\n    else\n      render 'new'\n    end\n  end\n\n  def edit\n    @note = Note.find(params[:id])\n  end\n\n  def update\n    @note = Note.find(params[:id])\n    if @note.update(note_params)\n      redirect_to @note\n    else\n      render 'edit'\n    end\n  end\n\n  def destroy\n    @note = Note.find(params[:id])\n    @note.destroy\n    redirect_to notes_path\n  end\n\n  private\n    def note_params\n      params.require(:note).permit(:title, :body)\n    end\nend