# Assuming you have Neo4j installed and the 'neo4j' gem added to your Gemfile\nrequire 'neo4j'\n\n# Connect to the Neo4j database\nNeo4j::Session.open(:server_db, 'http://localhost:7474', basic_auth: {username: 'neo4j', password: 'password'})\n\n# Create users\nuser_you = Neo4j::Node.create({name: 'YOU'}, :User)\nperson_b = Neo4j::Node.create({name: 'Person B'}, :User)\nperson_c = Neo4j::Node.create({name: 'Person C'}, :User)\nperson_z = Neo4j::Node.create({name: 'Person Z'}, :User)\n# ... Create other users similarly\n\n# Create relationships\nuser_you.create_rel(:CONNECTED_TO, person_b)\nperson_b.create_rel(:CONNECTED_TO, person_c)\nperson_c.create_rel(:CONNECTED_TO, person_z)\n# ... Create other relationships similarly