require 'fileutils'\n\n# Define input and output file paths\ninput_file = 'path/to/your/utf8_file.csv'\noutput_file = 'path/to/your/ansi_file.csv'\n\n# Read the UTF-8 encoded file\nutf8_content = File.read(input_file, encoding: 'UTF-8')\n\n# Convert the content to ANSI (Windows-1252)\nansi_content = utf8_content.encode('Windows-1252', invalid: :replace, undef: :replace, replace: '?')\n\n# Write the ANSI content to the output file\nFile.open(output_file, 'w:Windows-1252') do |file|\n  file.write(ansi_content)\nend\n\nputs "Conversion complete. The ANSI file has been saved to #{output_file}."