require 'test_helper'\nrequire 'action_view'\nrequire 'action_controller'\n\nclass MyTest < ActiveSupport::TestCase\n  include ActionView::TestCase::Behavior\n\n  def setup\n    @view = ActionView::Base.new(ActionController::Base.view_paths, {})\n    @view.extend(ApplicationHelper) # Include any helpers you need\n  end\n\n  def test_renders_partial\n    result = @view.render(partial: 'something')\n    assert_match /Expected content/, result\n  end\nend