# lib/tasks/send_test_emails.rake\nnamespace :mailing_list do\n  desc 'Send test emails to a list of addresses'\n  task send_test_emails: :environment do\n    emails = File.readlines("test_emails.txt").map(&:chomp)\n    subject = "Test Email"\n    body = "This is a test email."\n\n    emails.each do |email|\n      MailingListMailer.send_email(email, subject, body).deliver_now\n    end\n  end\nend