require 'optparse'\n\n# Parse command-line arguments\noptions = {}\nOptionParser.new do |opts|\n  opts.banner = "Usage: wrapper.rb [options]"\n\n  opts.on("-f", "--file FILE", "Input file") do |file|\n    options[:file] = file\n  end\nend.parse!\n\n# Ensure the file option is provided\nunless options[:file]\n  puts "Error: Input file is required"\n  exit 1\nend\n\n# Call the main script with the parsed arguments\nsystem("ruby main.rb #{options[:file]}")