# In your environment configuration file (config/environments/production.rb)\n\nrequire "smtp_tls" # This is necessary for older versions of Ruby\n\nActionMailer::Base.smtp_settings = {\n  :address              => "smtp.gmail.com",\n  :port                 => 587,\n  :domain               => "yourdomain.com", # Replace with your domain\n  :user_name            => "your_email@gmail.com", # Replace with your Gmail address\n  :password             => "your_password", # Replace with your Gmail password\n  :authentication       => :plain,\n  :enable_starttls_auto => true # This is important for TLS\n}