# Gemfile\n    gem 'pundit'\n\n    # app/policies/application_policy.rb\n    class ApplicationPolicy\n      attr_reader :user, :record\n\n      def initialize(user, record)\n        @user = user\n        @record = record\n      end\n\n      def index?\n        false\n      end\n      \n      def show?\n        scope.where(id: record.id).exists?\n      end\n\n      def create?\n        false\n      end\n\n      def new?\n        create?\n      end\n\n      def update?\n        false\n      end\n\n      def edit?\n        update?\n      end\n\n      def destroy?\n        false\n      end\n\n      class Scope\n        attr_reader :user, :scope\n\n        def initialize(user, scope)\n          @user = user\n          @scope = scope\n        end\n\n        def resolve\n          scope.all\n        end\n      end\n    end