# Sample arrays for start_time and end_time\nstart_time_array = [214, 222, 0, 0, 0]\nend_time_array = [214, 223, 0, 0, 0]\n\n# Function to convert array to packed string\ndef convert_to_packed_string(time_array)\n  trimmed_array = time_array.dup\n  trimmed_array.pop while trimmed_array.last == 0\n  trimmed_array.pack("C*")  # Use "C*" for unsigned char array\nend\n\n# Convert start_time and end_time arrays to packed strings\nstart_time = convert_to_packed_string(start_time_array)\nend_time = convert_to_packed_string(end_time_array)\n\n# Perform the query using ActiveRecord\nresults = Basic.where("start_time > ? AND end_time < ?", start_time, end_time)\n\n# Output the results\nresults.each do |record|\n  puts record.inspect\nend