require "gtk2"\n\n# Adds a page to the notebook with the given label\ndef create_page(nb, label="untitled")\n    # Create a textview\n    tx = Gtk::TextView.new\n    # Append it with a label\n    nb.append_page(tx, Gtk::Label.new(label))\n    # Show the newly created widget (important to ensure it is visible)\n    tx.show\nend\n\nGtk.init\nwindow = Gtk::Window.new\nwindow.set_default_size(800, 600)\nwindow.signal_connect("destroy") {\n    Gtk.main_quit\n}\n\ncontainer = Gtk::VBox.new\nnotebook = Gtk::Notebook.new\nbutton = Gtk::Button.new("New")\n\n# When the button is clicked, add a new page\nbutton.signal_connect("clicked") {\n    create_page(notebook)\n}\n\ncontainer.pack_start(button, false, false, 0)\ncreate_page(notebook)\ncontainer.pack_start(notebook, true, true, 0)\nwindow.add(container)\n\nwindow.show_all\nGtk.mai