class ReportsController < ApplicationController\n  def show\n    respond_to do |format|\n      format.html\n      format.pdf do\n        render pdf: "file_name",   # Ex: "file_name.pdf"\n               template: "reports/show.html.erb",\n               layout: "pdf.html", # Optional layout for PDF\n               show_as_html: params.key?('debug') # Allow debugging\n      end\n    end\n  end\nend