class YourController < ApplicationController\n  def create\n    @your_model = YourModel.new(your_model_params)\n    @your_model.resubmit = params[:resubmit].present?\n\n    if @your_model.save\n      redirect_to @your_model, notice: 'Model was successfully created.'\n    else\n      render :new\n    end\n  end\n\n  private\n\n  def your_model_params\n    params.require(:your_model).permit(:value1, :value2, :resubmit)\n  end\nend