require 'sinatra'\nrequire 'mail'\n\n# Configure Mail gem\nMail.defaults do\n  delivery_method :smtp, {\n    address: "localhost",\n    port: 25\n  }\nend\n\n# Route to handle the form POST request\npost '/remote.cgi' do\n  # Parse form parameters\n  name = params[:name]\n  email = params[:email]\n  message = params[:message]\n\n  # Send email\n  Mail.deliver do\n    from    email\n    to      'preset@example.com'\n    subject 'Contact Form Submission'\n    body    "Name: #{name}\nEmail: #{email}\nMessage: #{message}"\n  end\n\n  # Redirect to confirmation page\n  redirect '/confirmation.html'\nend\n\n# Route to show the confirmation page\nget '/confirmation.html' do\n  "Thank you for your message. We will get back to you soon."\nend