class DocumentsController < ApplicationController\n  def create\n    require 'docx'\n\n    # Create a new Document\n    doc = Docx::Document.new\n\n    # Add some text to the document\n    doc.paragraphs << 'Hello, this is a sample text in the Word document.'\n\n    # Save the document to a temporary file\n    temp_file = Tempfile.new(['output', '.docx'])\n    doc.save(temp_file.path)\n\n    # Send the file to the user\n    send_file temp_file.path, filename: 'output.docx', type: 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'\n  ensure\n    temp_file.close\n    temp_file.unlink\n  end\nend