require 'open-uri'\nrequire 'fileutils'\n\nurl = 'http://www.minscandboo.com/minscgame/001-Title01.jpg'\nfile_path = 'local_image.jpg'\n\nbegin\n  URI.open(url) do |image|\n    File.open(file_path, 'wb') do |file|\n      file.write(image.read)\n    end\n  end\n  puts "Image downloaded successfully to #{file_path}"\nrescue OpenURI::HTTPError => e\n  puts "Failed to download image: #{e.message}"\nend\n\n# Now you can use the downloaded image locally\n@sprite.bitmap = Bitmap.new(file_path)