class ResourcesController < ApplicationController\n  def destroy\n    resource = Resource.find(params[:id])\n    resource.update(deleted_at: Time.current)\n    # Code to move the resource to a trash table or mark it as deleted\n    render json: { message: "Resource destroyed" }, status: :ok\n  end\n\n  def restore\n    trashed_resource = TrashTable.find(params[:id])\n    trashed_resource.restore\n    # Code to move the resource back from the trash table or mark it as active\n    render json: { message: "Resource restored" }, status: :ok\n  end\nend