require 'nokogiri'\n\nhtml = <<-HTML\n<html>\n  <body>\n    <div id="menu">\n      <ul>\n        <li>Home</li>\n        <li>About</li>\n        <li>Contact</li>\n      </ul>\n    </div>\n  </body>\n</html>\nHTML\n\ndoc = Nokogiri::HTML(html)\n\n# Selecting elements using CSS selectors\nmenu = doc.css('#menu')\nputs menu.inner_html\n\n# Adding new elements\nmenu.add_child('<p>New item</p>')\n\n# Manipulating the DOM\nmenu.css('ul').first.add_child('<li>Blog</li>')\n\nputs doc.to_html