class Applicant < ActiveRecord::Base\n  belongs_to :order\n\n  with_options if: :company_200? do |applicant|\n    applicant.validates_length_of :middle_name, maximum: 20, allow_nil: true\n  end\n\n  with_options if: :company_201? do |applicant|\n    applicant.validates_length_of :middle_name, maximum: 1, allow_nil: true\n  end\n\n  private\n\n  def company_200?\n    order.company == 200\n  end\n\n  def company_201?\n    order.company == 201\n  end\nend