# app/serializers/game_serializer.rb\nclass GameSerializer < ActiveModel::Serializer\n  attributes :id, :name, :status_id\n  has_many :players\n  has_one :current_turn, if: -> { object.current_turn.present? }\nend\n\n# app/serializers/game_full_serializer.rb\nclass GameFullSerializer < ActiveModel::Serializer\n  attributes :id, :name, :status_id, :deck_id, :turn_num\n  has_many :players\n  has_many :turns\n  has_one :current_turn, if: -> { object.current_turn.present? }\nend\n\n# app/serializers/game_history_serializer.rb\nclass GameHistorySerializer < ActiveModel::Serializer\n  attributes :id, :name\n  has_many :turns\nend