# db/seeds.rb\n   themes = [\n     { name: 'Light', background_color: '#FFFFFF', text_color: '#000000' },\n     { name: 'Dark', background_color: '#000000', text_color: '#FFFFFF' },\n     { name: 'Blue', background_color: '#0000FF', text_color: '#FFFF00' }\n   ]\n\n   themes.each do |theme|\n     Theme.find_or_create_by(name: theme[:name]) do |t|\n       t.background_color = theme[:background_color]\n       t.text_color = theme[:text_color]\n     end\n   end