class RecipesController < ApplicationController\n  def import\n    @recipe = Recipe.find(params[:id])\n    \n    if @recipe.update_attributes(recipe_params)\n      render json: { status: 'success', content: render_to_string(partial: 'form', locals: { recipe: @recipe }) }\n    else\n      render json: { status: 'error', errors: @recipe.errors.full_messages }\n    end\n  end\n  \n  private\n  \n  def recipe_params\n    params.require(:recipe).permit(:name, :ingredients, :instructions)\n  end\nend