require 'csv'\n\n    class EntriesController < ApplicationController\n      def getcsv\n        @entries = Entry.all\n\n        csv_data = CSV.generate(headers: true) do |csv|\n          csv << ["First Name", "Last Name", "Created At", "Updated At"] # Add column headers as needed\n          \n          @entries.each do |entry|\n            csv << [entry.firstName, entry.lastName, entry.created_at, entry.updated_at] # Add entry attributes\n          end\n        end\n\n        send_data csv_data, filename: "entries_#{Date.today}.csv", type: 'text/csv', disposition: 'attachment'\n      end\n    end