# lib/tasks/export_to_yaml.rake\nnamespace :db do\n  desc "Export a database table to a YAML fixture"\n  task export_to_yaml: :environment do\n    table_name = ENV['TABLE']\n    file_path = ENV['FILE'] || "db/fixtures/#{table_name}.yml"\n\n    if table_name.nil?\n      puts "Please provide a table name using TABLE=table_name"\n      exit 1\n    end\n\n    records = ActiveRecord::Base.connection.execute("SELECT * FROM #{table_name}")\n    data = records.map { |record| record.attributes }\n\n    File.open(file_path, 'w') do |file|\n      file.write(data.to_yaml)\n    end\n\n    puts "Exported #{table_name} to #{file_path}"\n  end\nend