class SyncModelJob < ApplicationJob\n  queue_as :default\n\n  def perform(model_id)\n    model = MyModel.find(model_id)\n    response = HTTParty.post('https://secondary-app.com/api/my_models', \n                             body: model.to_json, \n                             headers: { 'Content-Type' => 'application/json' })\n    unless response.success?\n      Rails.logger.error("Failed to sync MyModel with ID: #{model_id}")\n    end\n  end\nend