class Api::MyModelsController < ApplicationController\n  protect_from_forgery with: :null_session\n\n  def create\n    my_model = MyModel.find_or_initialize_by(id: params[:id])\n    if my_model.update(my_model_params)\n      render json: { message: 'Synced successfully' }, status: :ok\n    else\n      render json: { errors: my_model.errors.full_messages }, status: :unprocessable_entity\n    end\n  end\n\n  private\n\n  def my_model_params\n    params.require(:my_model).permit(:id, :attribute_1, :attribute_2, :attribute_3, ...) # List all attributes\n  end\nend