require 'mysql2'\n\n# Establish connection to MySQL database\nclient = Mysql2::Client.new(\n  host: "localhost", \n  username: "username", \n  password: "password", \n  database: "database_name"\n)\n\n# Select data from the source table\nresults = client.query("SELECT myfield1, myfield2, id, blobfield FROM tablename")\n\n# Insert data into the destination table\nresults.each do |row|\n  client.query("INSERT INTO dest_table (field1, field2, field3, blobfield, field4) VALUES (\n    '#{client.escape(row['myfield1'])}', \n    '#{client.escape(row['myfield2'])}', \n    '#{row['id']}', \n    '#{client.escape(row['blobfield'])}', \n    'SomeConstant')")\nend\n\nclient.close