def create\n     @comment = Comment.new(comment_params)\n\n     respond_to do |format|\n       if @comment.save\n         flash[:notice] = 'Added comment.'\n         format.js\n       else\n         format.js { render partial: 'error', status: :unprocessable_entity }\n       end\n     end\n   end\n\n   private\n\n   def comment_params\n     params.require(:comment).permit(:comment)\n   end