class Person < ActiveRecord::Base\n  belongs_to :father, class_name: 'Person', optional: true\n  belongs_to :mother, class_name: 'Person', optional: true\n\n  has_many :children_through_mother, class_name: 'Person', foreign_key: 'mother_id'\n  has_many :children_through_father, class_name: 'Person', foreign_key: 'father_id'\n  \n  # Optionally, if you want a combined list of all children:\n  def children\n    Person.where('mother_id = ? OR father_id = ?', self.id, self.id)\n  end\nend