require 'fileutils'\n\ndef get_immediate_subdirectories(directory)\n  Dir.entries(directory).select do |entry|\n    path = File.join(directory, entry)\n    File.directory?(path) && !(entry == '.' || entry == '..')\n  end\nend\n\ndef copy_file_to_subdirectories(file, directory)\n  subdirectories = get_immediate_subdirectories(directory)\n  subdirectories.each do |subdir|\n    destination = File.join(directory, subdir, File.basename(file))\n    FileUtils.cp(file, destination)\n  end\nend\n\n# Example usage\ndirectory = '/path/to/parent_directory'\nfile = '/path/to/file_to_copy.txt'\n\ncopy_file_to_subdirectories(file, directory)