class User < ApplicationRecord\n  scope :upcoming_birthdays, ->(limit = 5) {\n    where("EXTRACT(MONTH FROM date_of_birth) = ? AND EXTRACT(DAY FROM date_of_birth) >= ?", Date.today.month, Date.today.day)\n    .or(where("EXTRACT(MONTH FROM date_of_birth) = ? AND EXTRACT(DAY FROM date_of_birth) < ?", Date.today.next_month.month, Date.today.next_month.day))\n    .order("EXTRACT(MONTH FROM date_of_birth), EXTRACT(DAY FROM date_of_birth)")\n    .limit(limit)\n  }\nend