class ConceptsController < ApplicationController\n  before_action :set_status, only: [:create, :update]\n\n  def create\n    @concept = Concept.new(concept_params)\n    # other create logic\n  end\n\n  def update\n    @concept = Concept.find(params[:id])\n    @concept.update(concept_params)\n    # other update logic\n  end\n\n  private\n\n  def set_status\n    consulted_legal = params[:concept][:consulted_legal].to_i\n    consulted_marketing = params[:concept][:consulted_marketing].to_i\n\n    status = case [consulted_legal, consulted_marketing]\n             when [0, 1]\n               'Awaiting Compliance Approval'\n             when [1, 0]\n               'Awaiting Marketing Approval'\n             when [0, 0]\n               'Awaiting Marketing & Legal Approval'\n             else\n               'Pending Approval'\n             end\n\n    @concept.attributes = { status: status }\n  end\n\n  def concept_params\n    # Strong parameters for your concept\n    params.require(:concept).permit(:consulted_legal, :consulted_marketing, :other_attributes)\n  end\nend