require 'openssl'\nrequire 'base64'\n\nkey = 'thisisaverysecurekey12345678' # 32 bytes key for AES-256\niv = 'thisisinitialvect' # 16 bytes IV for AES-256\n\ndef encrypt(data, key, iv)\n  cipher = OpenSSL::Cipher::AES.new(256, :CBC)\n  cipher.encrypt\n  cipher.key = key\n  cipher.iv = iv\n  encrypted = cipher.update(data) + cipher.final\n  Base64.encode64(encrypted).strip\nend\n\ndata = "Hello from Ruby!"\nencrypted_data = encrypt(data, key, iv)\nputs encrypted_data