class ClientRegionsController < ApplicationController\n  make_resourceful do\n    actions :all\n    belongs_to :client\n\n    response_for(:create) do |format|\n      format.html { redirect_to client_client_regions_path(parent_object) }\n    end\n\n    response_for(:update) do |format|\n      format.html { redirect_to client_client_regions_path(parent_object) }\n    end\n  end\n\n  private\n\n  def current_objects\n    @current_objects ||= parent_object.client_regions.paginate(page: params[:page], order: "name")\n  end\n\n  def parent_object\n    @parent_object ||= Client.find(params[:client_id])\n  end\nend