class Product < ApplicationRecord\n  # Assuming you're using permalink_fu or similar gem for permalinks\n  before_validation :generate_permalink\n\n  validate :permalink_must_not_overlap_with_reserved_routes\n\n  RESERVED_PATHS = %w[specials new-in-stock new edit].freeze\n\n  private\n\n  def generate_permalink\n    # Your permalink generation logic here\n  end\n\n  def permalink_must_not_overlap_with_reserved_routes\n    if RESERVED_PATHS.include?(permalink)\n      errors.add(:permalink, 'is reserved and cannot be used')\n    end\n  end\nend