describe Item do\n      describe '#most_expensive' do\n        [\n          { prices: [10, 20, 30], expected: 30 },\n          { prices: [5, 5, 5], expected: 5 },\n          { prices: [1, 100, 50], expected: 100 }\n        ].each do |data|\n          it "returns the most expensive item for prices #{data[:prices]}" do\n            items = data[:prices].map { |price| Item.create(price: price) }\n            expect(Item.most_expensive.price).to eq(data[:expected])\n          end\n        end\n      end\n    end