# lib/tasks/send_bulk_emails.rake\n    namespace :emails do\n      desc "Send bulk emails to users"\n      task send_bulk: :environment do\n        list_id = 'your-mailchimp-list-id'\n        campaign = HOMINID.create_campaign(\n          'regular',\n          {\n            'list_id' => list_id,\n            'subject' => 'Your Subject Here',\n            'from_email' => 'your-email@example.com',\n            'from_name' => 'Your Name'\n          },\n          {\n            'html' => File.read('path/to/your/email_template.html')\n          }\n        )\n        HOMINID.campaign_send_now(campaign['id'])\n      rescue => e\n        puts "An error occurred: #{e.message}"\n      end\n    end