CREATE OR REPLACE FUNCTION create_yearly_tables()\nRETURNS void LANGUAGE plpgsql AS $$\nDECLARE\n    year TEXT;\n    next_year TEXT;\nBEGIN\n    -- Get the current year and the next year\n    year := to_char(current_date, 'YYYY');\n    next_year := to_char(current_date + interval '1 year', 'YYYY');\n\n    -- Prepare the table names\n    EXECUTE 'CREATE TABLE orders_' || next_year || ' (LIKE orders_' || year || ' INCLUDING ALL)';\n    EXECUTE 'CREATE TABLE items_' || next_year || ' (LIKE items_' || year || ' INCLUDING ALL)';\n\n    -- Add primary keys\n    EXECUTE 'ALTER TABLE ONLY orders_' || next_year || ' ADD CONSTRAINT orders_' || next_year || '_pkey PRIMARY KEY (id)';\n    EXECUTE 'ALTER TABLE ONLY items_' || next_year || ' ADD CONSTRAINT items_' || next_year || '_pkey PRIMARY KEY (id)';\n\n    -- Add foreign key constraint\n    EXECUTE 'ALTER TABLE ONLY items_' || next_year || ' ADD CONSTRAINT fk_order_id FOREIGN KEY (order_id) REFERENCES orders_' || next_year || '(id) ON DELETE CASCADE';\nEND;\n$$;