CREATE OR REPLACE TRIGGER my_table_before_update\nBEFORE UPDATE ON my_table\nFOR EACH ROW\nDECLARE\n    v_identifier VARCHAR2(30);\nBEGIN\n    -- Retrieve the session identifier\n    SELECT SYS_CONTEXT('USERENV', 'CLIENT_IDENTIFIER') INTO v_identifier FROM DUAL;\n    \n    IF v_identifier = 'APPLICATION_SESSION' THEN\n        -- allow update\n        NULL; \n    ELSE\n        -- raise an error\n        RAISE_APPLICATION_ERROR(-20001, 'You are not allowed to update this table.');\n    END IF;\nEND;\n/