-- Step 1: Store your result set in a temporary table\nSELECT *\nINTO #TempResult\nFROM (\n    -- Your ad hoc query here\n    SELECT column1, column2, column3\n    FROM your_table\n    WHERE your_conditions\n) AS ResultSet;\n\n-- Step 2: Generate the INSERT statements\nDECLARE @SQL NVARCHAR(MAX) = N'';\nDECLARE @TableName NVARCHAR(128) = '#TempResult';\nDECLARE @CRLF NVARCHAR(2) = CHAR(13) + CHAR(10);\n\nSELECT @SQL = @SQL +\n    'INSERT INTO ' + @TableName + ' (' + STUFF((\n        SELECT ', ' + QUOTENAME(c.name)\n        FROM tempdb.sys.columns AS c\n        WHERE c.object_id = OBJECT_ID('tempdb..#TempResult')\n        FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 2, '') + ')' + @CRLF +\n    'VALUES (' + STUFF((\n        SELECT ', ' + \n        CASE \n            WHEN c.is_nullable = 1 AND t.name IN ('varchar', 'nvarchar', 'text', 'ntext') THEN 'NULLIF(' + QUOTENAME(c.name) + ', '''')'\n            WHEN c.is_nullable = 1 AND t.name IN ('int', 'bigint', 'smallint', 'tinyint', 'decimal', 'numeric', 'float', 'real') THEN 'NULLIF(' + QUOTENAME(c.name) + ', 0)'\n            ELSE QUOTENAME(c.name)\n        END\n        FROM tempdb.sys.columns AS c\n        JOIN tempdb.sys.types AS t ON c.user_type_id = t.user_type_id\n        WHERE c.object_id = OBJECT_ID('tempdb..#TempResult')\n        FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 2, '') + ');' + @CRLF\nFROM #TempResult;\n\n-- Step 3: Print or execute the generated SQL script\nPRINT @SQL;\n\n-- Optionally, if you want to execute the INSERT statements in a different session\n-- EXEC sp_executesql @SQL;\n\n-- Clean up\nDROP TABLE #TempResult;