CREATE PROCEDURE dbo.FindGUID\n    @searchValue uniqueidentifier\nAS\nBEGIN\n    DECLARE @sql NVARCHAR(MAX) = ''\n\n    SELECT @sql = @sql + 'SELECT ''' + c.TABLE_NAME + ''' AS TheTable, * FROM ' + c.TABLE_NAME + ' WHERE ' + \n                    STUFF((SELECT ' OR ' + COLUMN_NAME + ' = ''' + CAST(@searchValue AS VARCHAR(50)) + ''''\n                           FROM INFORMATION_SCHEMA.Columns\n                           WHERE TABLE_NAME = c.TABLE_NAME\n                             AND DATA_TYPE = 'uniqueidentifier'\n                           FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 4, '') + '; '\n    FROM (SELECT DISTINCT TABLE_NAME \n          FROM INFORMATION_SCHEMA.Columns \n          WHERE DATA_TYPE = 'uniqueidentifier') c\n\n    PRINT @sql\n    EXEC sp_executesql @sql\nEND