SELECT  cols.column_name AS ForeignKeyField,\n        r_cols.column_name AS PrimaryKeyField,\n        r_cons.table_name AS PrimaryTable\nFROM    all_cons_columns cols\n        JOIN all_constraints cons ON cols.constraint_name = cons.constraint_name\n        JOIN all_constraints r_cons ON cons.r_constraint_name = r_cons.constraint_name\n        JOIN all_cons_columns r_cols ON r_cons.constraint_name = r_cols.constraint_name\nWHERE   cons.constraint_type = 'R' \nAND     cons.table_name = :name\nAND     cols.position = r_cols.position;