WITH LatestTransactions AS (\n    SELECT\n        a.TransactionID,\n        a.QuestionID,\n        a.Answer,\n        t.ShopID,\n        t.Created,\n        ROW_NUMBER() OVER (\n            PARTITION BY a.QuestionID\n            ORDER BY t.Created DESC\n        ) AS rn\n    FROM\n        tblAnswer a\n    JOIN\n        tblTransaction t ON a.TransactionID = t.TransactionID\n    WHERE\n        t.ShopID = 'Some Shop Plc.'\n)\nSELECT\n    TransactionID,\n    QuestionID,\n    Answer,\n    ShopID,\n    Created\nFROM\n    LatestTransactions\nWHERE\n    rn = 1;