CREATE TABLE FactRole (\n        RoleID INT PRIMARY KEY,\n        DateKey INT,\n        RoleTypeID INT,\n        OrganizationID INT,\n        PersonID INT,\n        FromDate DATE,\n        ToDate DATE,\n        Count INT,\n        FOREIGN KEY (DateKey) REFERENCES DimDate(DateKey),\n        FOREIGN KEY (RoleTypeID) REFERENCES DimRoleType(RoleTypeID),\n        FOREIGN KEY (OrganizationID) REFERENCES DimOrganization(OrganizationID),\n        FOREIGN KEY (PersonID) REFERENCES DimPerson(PersonID)\n    );