DECLARE\n  CURSOR temp_cursor IS SELECT id FROM temp_table;\n  TYPE id_table_type IS TABLE OF temp_table.id%TYPE INDEX BY PLS_INTEGER;\n  id_table id_table_type;\n  vCT NUMBER(38) := 0;\n  c_limit PLS_INTEGER := 200000;  -- Commit every 200,000 rows\nBEGIN\n  OPEN temp_cursor;\n  LOOP\n    FETCH temp_cursor BULK COLLECT INTO id_table LIMIT c_limit;\n    EXIT WHEN id_table.COUNT = 0;\n    \n    FORALL i IN 1 .. id_table.COUNT\n      DELETE FROM table_name WHERE id = id_table(i);\n      \n    vCT := vCT + id_table.COUNT;\n    COMMIT;\n  END LOOP;\n  CLOSE temp_cursor;\nEND;\n/