-- Create Table\nCREATE TABLE mytable (\n    col1 INT,\n    col2 VARCHAR(50),\n    wholenodecolumn XML\n);\n\n-- Input XML\nDECLARE @xml XML = '\n<xml>\n  <Items>\n    <Item>\n        <node1>1</node1>\n        <node2>abc</node2>\n    </Item>\n    <Item>\n        <node1>2</node1>\n        <node2>def</node2>\n    </Item>\n  </Items>\n</xml>';\n\n-- Insert Data\nINSERT INTO mytable (col1, col2, wholenodecolumn)\nSELECT  \n    doc.col.value('node1[1]', 'int') AS column1,\n    doc.col.value('node2[1]', 'varchar(50)') AS column2,\n    doc.col.query('.') AS wholenodecolumn\nFROM @xml.nodes('//Items/Item') AS doc(col);\n\n-- Update to add root node <Item>\nUPDATE mytable\nSET wholenodecolumn = (\n    SELECT \n        wholenodecolumn.query('<Item>{.}</Item>')\n    FROM mytable AS t\n    WHERE t.col1 = mytable.col1\n        AND t.col2 = mytable.col2\n);