USE master;\n   DECLARE @dbname NVARCHAR(128) = 'YourDatabaseName';\n   \n   WHILE EXISTS (\n       SELECT 1\n       FROM sys.dm_exec_sessions s\n       JOIN sys.dm_exec_requests r ON s.session_id = r.session_id\n       WHERE s.database_id = DB_ID(@dbname) AND s.is_user_process = 1\n   )\n   BEGIN\n       PRINT 'Waiting for regular users to disconnect...';\n       WAITFOR DELAY '00:00:05';  -- Wait for 5 seconds before checking again\n   END\n\n   PRINT 'All regular users are disconnected.';