-- Drop all foreign key constraints\nDECLARE @name NVARCHAR(128)\nDECLARE @constraint NVARCHAR(128)\nDECLARE @SQL NVARCHAR(254)\n\n-- Disable foreign key constraints\nDECLARE constraints_cursor CURSOR FOR\nSELECT name, OBJECT_NAME(parent_object_id)\nFROM sys.foreign_keys\n\nOPEN constraints_cursor\nFETCH NEXT FROM constraints_cursor INTO @constraint, @name\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQL = 'ALTER TABLE [' + @name + '] DROP CONSTRAINT [' + @constraint + ']'\n    EXEC sp_executesql @SQL\n    FETCH NEXT FROM constraints_cursor INTO @constraint, @name\nEND\n\nCLOSE constraints_cursor\nDEALLOCATE constraints_cursor\n\n-- Drop all tables\nDECLARE tables_cursor CURSOR FOR\nSELECT name\nFROM sys.tables\n\nOPEN tables_cursor\nFETCH NEXT FROM tables_cursor INTO @name\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQL = 'DROP TABLE [' + @name + ']'\n    EXEC sp_executesql @SQL\n    FETCH NEXT FROM tables_cursor INTO @name\nEND\n\nCLOSE tables_cursor\nDEALLOCATE tables_cursor\n\n-- Drop all views\nDECLARE views_cursor CURSOR FOR\nSELECT name\nFROM sys.views\n\nOPEN views_cursor\nFETCH NEXT FROM views_cursor INTO @name\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQL = 'DROP VIEW [' + @name + ']'\n    EXEC sp_executesql @SQL\n    FETCH NEXT FROM views_cursor INTO @name\nEND\n\nCLOSE views_cursor\nDEALLOCATE views_cursor\n\n-- Drop all stored procedures\nDECLARE procedures_cursor CURSOR FOR\nSELECT name\nFROM sys.procedures\n\nOPEN procedures_cursor\nFETCH NEXT FROM procedures_cursor INTO @name\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQL = 'DROP PROCEDURE [' + @name + ']'\n    EXEC sp_executesql @SQL\n    FETCH NEXT FROM procedures_cursor INTO @name\nEND\n\nCLOSE procedures_cursor\nDEALLOCATE procedures_cursor\n\n-- Drop all functions\nDECLARE functions_cursor CURSOR FOR\nSELECT name\nFROM sys.objects\nWHERE type IN ('FN', 'IF', 'TF')\n\nOPEN functions_cursor\nFETCH NEXT FROM functions_cursor INTO @name\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQL = 'DROP FUNCTION [' + @name + ']'\n    EXEC sp_executesql @SQL\n    FETCH NEXT FROM functions_cursor INTO @name\nEND\n\nCLOSE functions_cursor\nDEALLOCATE functions_cursor\n\n-- Drop all fulltext catalogs\nDECLARE catalogs_cursor CURSOR FOR\nSELECT name\nFROM sys.fulltext_catalogs\n\nOPEN catalogs_cursor\nFETCH NEXT FROM catalogs_cursor INTO @name\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQL = 'DROP FULLTEXT CATALOG [' + @name + ']'\n    EXEC sp_executesql @SQL\n    FETCH NEXT FROM catalogs_cursor INTO @name\nEND\n\nCLOSE catalogs_cursor\nDEALLOCATE catalogs_cursor\n\n-- Drop all users (skipping default users)\nDECLARE users_cursor CURSOR FOR\nSELECT name\nFROM sys.database_principals\nWHERE type IN ('S', 'U', 'G') AND name NOT IN ('dbo', 'guest', 'sys', 'INFORMATION_SCHEMA')\n\nOPEN users_cursor\nFETCH NEXT FROM users_cursor INTO @name\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQL = 'DROP USER [' + @name + ']'\n    EXEC sp_executesql @SQL\n    FETCH NEXT FROM users_cursor INTO @name\nEND\n\nCLOSE users_cursor\nDEALLOCATE users_cursor\n\n-- Optionally, you might also want to drop roles, schemas, etc., depending on your requirements.