-- Backup your table\nCREATE TABLE your_table_backup AS SELECT * FROM your_table;\n\n-- Set the character set to latin1 to read the data correctly\nSET NAMES latin1;\n\n-- Convert the data from latin1 to utf8\nUPDATE your_table \nSET your_column = CONVERT(BINARY CONVERT(your_column USING latin1) USING utf8);\n\n-- Change the column character set to utf8\nALTER TABLE your_table MODIFY your_column VARCHAR(255) CHARACTER SET utf8;\n\n-- Verify the conversion\nSET NAMES utf8;\nSELECT * FROM your_table;